-- ----------------------------
-- Table structure for Read
-- ----------------------------
DROP TABLE IF EXISTS `Read`;
CREATE TABLE `Read`  (
  `article_id` int(11) NOT NULL,
  `usr_open_id` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `read_time` varchar(255) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  PRIMARY KEY (`article_id`, `usr_open_id`) USING BTREE,
