-- ----------------------------
-- Table structure for Users
-- ----------------------------
DROP TABLE IF EXISTS `Users`;
CREATE TABLE `Users`  (
  `usr_open_id` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NOT NULL,
  `usr_name` varchar(50) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `usr_pwd` varchar(200) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL,
  `usr_authority` int(11) NULL DEFAULT NULL,
  PRIMARY KEY (`usr_open_id`) USING BTREE,
  INDEX `ix_Users_usr_open_id`(`usr_open_id`) USING BTREE,
  INDEX `ix_Users_usr_name`(`usr_name`) USING BTREE,
  INDEX `ix_Users_usr_pwd`(`usr_pwd`) USING BTREE
) ENGINE = InnoDB CHARACTER SET = utf8 COLLATE = utf8_general_ci ROW_FORMAT = Dynamic;

